import java.awt.event.*;import java.awt.*;import javax.swing.*;public class Tutorial implements ActionListener{ Board parent; Timer timer; int[] deathtime = new int[]{4,5,5,5,5,5,2}; int lives = deathtime.length; int[] lifetime = new int[lives]; int incarnation = 0; public Tutorial(Board parenti){  parent = parenti;  timer = new Timer(1000,this);  timer.start(); } public void actionPerformed(ActionEvent e){  if (incarnation>=lives){   parent.start();   timer.stop();   return;  }  lifetime[incarnation]++;  if (lifetime[incarnation]>=deathtime[incarnation]){   incarnation++;  } } public void draw(Graphics g){  int halfwidth = (int)parent.parent.getSize().getWidth()/2;  int halfheight = (int)parent.parent.getSize().getHeight()/2;  Color black = new Color(0,0,0);  g.setColor(black);  Color fill = new Color(30,175,150);  if (incarnation==0){   g.drawRect(halfwidth-125,halfheight+50,251,30);   g.setColor(fill);   g.fillRect(halfwidth-124,halfheight+51,250,29);   g.setColor(black);   g.drawString("Use the WASD or arrow keys to move around.",halfwidth-123,halfheight+64);   g.drawString("Press Esc to skip this.",halfwidth-62,halfheight+77);  } else if (incarnation==1){   g.drawRect(halfwidth-156,halfheight+50,312,44);   g.setColor(fill);   g.fillRect(halfwidth-155,halfheight+51,311,43);   g.setColor(black);   g.drawString("Press space to attack the direction you're facing.",halfwidth-133,halfheight+64);   g.drawString("You gain gold whenever you or your minions do damage.",halfwidth-154,halfheight+77);   g.drawString("You lose gold when you take damage.",halfwidth-104,halfheight+90);  } else if (incarnation==2){   g.drawRect(halfwidth-156,halfheight+50,310,44);   g.setColor(fill);   g.fillRect(halfwidth-155,halfheight+51,309,43);   g.setColor(black);   g.drawString("Gold is spent to place Minions, shown in the bottom bar.",halfwidth-154,halfheight+64);   g.drawString("Press 'q' and 'r' to cycle through them.",halfwidth-105,halfheight+77);   g.drawString("Then press 'e' to place them or 'E' to remove them.",halfwidth-145,halfheight+90);  } else if (incarnation==3){   g.drawRect(halfwidth-148,halfheight+50,296,44);   g.setColor(fill);   g.fillRect(halfwidth-147,halfheight+51,295,43);   g.setColor(black);   g.drawString("The enemies that attack are based on your total value.",halfwidth-146,halfheight+64);   g.drawString("Value is the sum of your gold and the value of all of",halfwidth-139,halfheight+77);   g.drawString("your minions.",halfwidth-35,halfheight+90);  } else if (incarnation==4){   g.drawRect(halfwidth-127,halfheight+50,254,44);   g.setColor(fill);   g.fillRect(halfwidth-126,halfheight+51,253,43);   g.setColor(black);   g.drawString("On the bottom-right is your mini-map.",halfwidth-97,halfheight+64);   g.drawString("You are shown in blue, your minions in green,",halfwidth-125,halfheight+77);   g.drawString("and enemies are shown in red.",halfwidth-82,halfheight+90);  } else if (incarnation==5){   g.drawRect(halfwidth-148,halfheight+50,297,44);   g.setColor(fill);   g.fillRect(halfwidth-147,halfheight+51,296,43);   g.setColor(black);   g.drawString("Press 'p' to pause the game.",halfwidth-77,halfheight+64);   g.drawString("While the game is paused you may pan your view with",halfwidth-146,halfheight+77);   g.drawString("the regular movement controls.",halfwidth-84,halfheight+90);  } else if (incarnation==6){   g.drawRect(halfwidth-55,halfheight+50,111,18);   g.setColor(fill);   g.fillRect(halfwidth-54,halfheight+51,110,17);   g.setColor(black);   g.drawString("Let the game begin.",halfwidth-53,halfheight+64);  } } public void end(){  incarnation=lives; }}